package DemoTest.shop.service;import DemoTest.shop.model.Order;import DemoTest.shop.repository.OrderRepository;import org.springframework.amqp.rabbit.core.RabbitTemplate;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class OrderService {	private final OrderRepository orderRepository;	private final RabbitTemplate rabbitTemplate;	public OrderService(OrderRepository orderRepository, RabbitTemplate rabbitTemplate) {		this.orderRepository = orderRepository;		this.rabbitTemplate = rabbitTemplate;	}	public Order createOrder(Order order) {		Order savedOrder = orderRepository.save(order);		rabbitTemplate.convertAndSend("orderQueue", savedOrder.getId());		return savedOrder;	}	public List<Order> getOrders(){		return orderRepository.findAll();	}}